
public class Demo10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int a=10; int b=20;
       System.out.println("Before Swapping");
       System.out.println("a----->" +a);
       System.out.println("b----->"+b);
       a=a/b;
       b=a*b;
       a=b*a;
       System.out.println("after swapping");
       System.out.println("a---->"+a);
       System.out.println("b---->"+b);
	}

}
