
public class Demo5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  for(int r=1;r<=5;r++) {
	  for(int c=1;c<=r;c++) {
		  System.out.print(c+ " ");
	  }
       System.out.println();
  }

for (int r=5;r>=1;r--) {
	for(int c=5;c>=r;c--) {
		System.out.print(c+ " ");
	}
	System.out.println();
}
	}
}

